            'status'            => 'processing',
            'amount'            =>  0.99,
            'orderid'           =>  self::PAYNET_PAYMENT_ID,
            'merchant_order'    =>  self::CLIENT_PAYMENT_ID,
            'client_orderid'    =>  self::CLIENT_PAYMENT_ID,
        )));
    }

