    /**
     * {@inheritdoc}
     */
    protected function getOrder()
    {
        $order = new Order(array
        (
            'client_orderid'        => self::CLIENT_ORDER_ID,
            'paynet_order_id'       => self::PAYNET_ORDER_ID,
            'amount'                => 99.1,
            'currency'              => 'EUR'
        ));

        $order->setComment('cancel order');

